insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values ('Аватар 2 Путь воды', 'Бывший морпех Джейк Салли прикован к инвалидному креслу. Несмотря на немощное тело, Джейк в душе по-прежнему остается воином. Он получает задание совершить путешествие в несколько световых лет к базе землян на планете Пандора, где корпорации добывают редкий минерал, имеющий огромное значение для выхода Земли из энергетического кризиса.', 2022, 2, 16, 162, 2);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values ('Кот в сапогах 2 Последнее желание', 'Промотав восемь жизней из девяти и до смерти испугавшись пришедшего за ним волка-охотника за головами, Кот в сапогах внимает совету врача и заселяется в дом местной кошатницы. Там он смиряется с установленными порядками, отращивает бороду и теряет тягу к подвигам и приключениям, но только до тех пор, пока не узнаёт о волшебной карте, которая указывает путь к где-то упавшей Звезде желаний. Кот в сапогах твёрдо решает отыскать Звезду, чтобы вернуть свои жизни, и в компании притворявшегося кошачьим пёсика отправляется красть карту у известного коллекционера волшебных предметов Джека Хорнера.', 2022, 1, 12, 102, 1);
insert into films(name, description, "year", genre_id, minimal_age, duration_in_minutes, file_id) values ('Одни из нас', 'Закалённый в борьбе за выживание Джоэл и Элли — отважная, не по годам смышлёная девушка-подросток — вынуждены объединиться и помогать друг другу в странствиях по руинам Соединённых Штатов.', 2023, 2, 18, 50, 3);